import React, { Component } from 'react'

export default class Collapse extends Component {
    state = {
        showContent: true
    }

    render() {
        return (
            <div>
                <button class="btn btn-info">
                    {this.props.linkTitle}
                </button>

                {
                    this.state.showContent ? (
                        <div class="collapse show">
                            <div class="card card-body">
                                {this.props.children}
                            </div>
                        </div>
                    ) : null
                }



            </div>
        );
    }
}
